'use strict'
import React from 'react';
import classes from './index.module.css';
import llrh from '../../Lib/llrh.jpg';
import bbw from '../../Lib/bbw.jpg';
import {MDBBtn, MDBCard,MDBIcon, MDBModal, MDBModalBody} from 'mdbreact';

//------------------------SPEECH RECOGNITION-----------------------------

const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
const recognition = new SpeechRecognition();

recognition.continous = true;
recognition.interimResults = true;
recognition.lang = 'en-US';


export class AskQuestion extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            modal1: false,
            modal2: false,
            redQuestion:'',
            wolfQuestion:'',
            answer:'',
            change:false,
            tag:1,
            defaultQuestion:'',
            listening: false,
            speechState:'Click to start...',
            hints:''

        };
        this.toggleListen = this.toggleListen.bind(this)
        this.handleListen = this.handleListen.bind(this)

    }
    myClick=(text)=>{
        alert(text);
    }

    toggle = nr => () => {
        let modalNumber = 'modal' + nr
        this.setState({
            [modalNumber]: !this.state[modalNumber]
        });
    }
    searchAnswer=(value)=>{
        const option={
            method:'POST',
            headers: {
                'content-type': 'application/json',
            },
            body:JSON.stringify({"question":value})
        };
        fetch('http://127.0.0.1:5000/getanswer',option)
            .then(response=>response.text())
            .then(answer=>{
                if (answer.substr(0, 1) === '0'){


                    this.setState({
                        hints:'Is this the question you want to ask?',
                        answer:answer.substring(1,answer.length),
                        tag:0,
                        defaultQuestion:answer.substring(1,answer.length)
                    })
                }
                else{
                    this.setState({
                        answer:'Answer:'+answer.substring(1,answer.length)
                    })
                }

            })
            .catch(5000)
    }
    searchAgain=(answer)=>{
        const newoption={
            method:'POST',
            headers: {
                'content-type': 'application/json',
            },
            body:JSON.stringify({"question":answer})
        };
        fetch('http://127.0.0.1:5000/getanswer',newoption)
            .then(response=>response.text())
            .then(newanswer=>{
                this.setState({
                    answer:newanswer.substring(1,newanswer.length),
                    tag:1,
                    hints:''
                })
            })
    };
    //--------------Speech Recognition--------------
    toggleListen() {
        this.setState({
            listening: !this.state.listening
        }, this.handleListen)
    }

    handleListen() {

        console.log('listening?', this.state.listening)

        if (this.state.listening) {
            recognition.start();
            recognition.onend = () => {
                this.setState({
                    speechState:'...continue listening...'
                });
                recognition.start()
            }

        } else {
            recognition.stop();
            recognition.onend = () => {
                this.setState({
                    speechState:'Click to start...'
                });
            }
        }

        recognition.onstart = () => {
            this.setState({
                speechState:'Listening...Click to pause'
            });
        }

        let finalTranscript = '';
        recognition.onresult = event => {
            let interimTranscript = '';

            for (let i = event.resultIndex; i < event.results.length; i++) {
                const transcript = event.results[i][0].transcript;
                if (event.results[i].isFinal) finalTranscript += transcript + ' ';
                else interimTranscript += transcript;
            }
            if (this.state.modal1){
                this.setState({
                    redQuestion:finalTranscript
                })
            }
            else if(this.state.modal2){
                this.setState({
                    wolfQuestion:finalTranscript
                })
            }
            document.getElementById('interim').innerHTML = interimTranscript;
            document.getElementById('final').value = finalTranscript;

            //-------------------------COMMANDS------------------------------------

            const transcriptArr = finalTranscript.split(' ');
            const stopCmd = transcriptArr.slice(-3, -1);
            console.log('stopCmd', stopCmd);

            if (stopCmd[0] === 'stop' && stopCmd[1] === 'listening'){
                recognition.stop();
                recognition.onend = () => {
                    console.log('Stopped listening per command');
                    const finalText = transcriptArr.slice(0, -3).join(' ');
                    document.getElementById('final').value = finalText
                }
            }
        }

        //-----------------------------------------------------------------------

        recognition.onerror = event => {
            console.log("Error occurred in recognition: " + event.error)
        }

    }

